const getDateString = e => {
    try {
      e || (e = new Date);
      let t = e.getFullYear(),
        r = e.getMonth() + 1;
      r = r < 10 ? `0${r}` : r;
      let a = e.getDate();
      a = a < 10 ? `0${a}` : a;
      let l = e.getHours();
      l = l < 10 ? `0${l}` : l;
      let g = e.getMinutes();
      g = g < 10 ? `0${g}` : g;
      let n = e.getSeconds();
      return n = n < 10 ? `0${n}` : n, `${t}-${r}-${a} ${l}:${g}:${n}`
    } catch (e) {
      return console.log(e.message), null
    }
  },
  getDateByFormat = (e, t) => {
    e = getDateString(e);
    switch (t) {
      case "YYYY-MM-DD HH:MM:SS":
        return e;
      case "YYYY-MM-DD":
        return e.substr(0, 10);
      case "YYYYMMDD":
        return e.substr(0, 10).replace(/-/gi, "");
      case "YYYYMMDD_HHMMSS":
        return e.replace(/-/gi, "").replace(/:/gi, "").replace(" ", "_");
      default:
        return e.substr(0, 10)
    }
  },
  getDateCalculator = e => {
    let t;
    t = e ? new Date(e) : new Date;
    let r = t.getFullYear(),
      a = t.getMonth() + 1;
    a = a < 10 ? `0${a}` : a;
    let l = t.getDate();
    l = l < 10 ? `0${l}` : l;
    let g = t.getHours();
    g = g < 10 ? `0${g}` : g;
    let n = t.getMinutes();
    n = n < 10 ? `0${n}` : n;
    let s = t.getSeconds();
    return s = s < 10 ? `0${s}` : s, `${r}-${a}-${l} ${g}:${n}:${s}`
  };
module.exports = {
  dateCal: getDateCalculator,
  dateStr: getDateString,
  dateFormat: getDateByFormat
};